Set {name=value;}
}
Public PageReference query() {
String qryString= 'SELECT Id FROM Contact WHERE' +
'(IsDeleted = false and Name like \'#' + name + '#\')';
queryResult = Database.query{qryString};
return null;
}
}
Choose 2 answers.
